import React from "react";
import PopUp from "./PopUp";
import { useState } from "react";
import Popup from "reactjs-popup";
import "reactjs-popup/dist/index.css";

function Display({ apiDataP, index }) {
  const [imagePop, setImagePop] = useState(false);

  console.log(apiDataP);
  console.log("hello");
  function getPopUpData(e) {
    e.preventDefault();
    console.log("im in");
    setImagePop(true);
  }

  return (
    <div>
      <div className="row-list" key={index}>
      <img
          className="img"
          onClick={(e) => getPopUpData(e)}
          src={apiDataP.urls.thumb}
          alt="img"
        ></img>
          <Popup trigger={<button> <img
            className="img"
            onClick={(e) => getPopUpData(e)}
            src={apiDataP.urls.thumb}
            alt="img"
          ></img></button>} position="right center">
            <div>Popup content here !!</div>
          </Popup>
        ) : (
          ""
        )}
      </div>
    </div>
  );
}

export default Display;
