import React from "react";
import { Instagram,Twitter,ThumbUpOutlined, CloudDownloadOutlined } from "@material-ui/icons";

function PopUp({ getPopUpData, apiDataP }) {

    const downloadURL = (url) => {
        const urldata = url;
        window.open(urldata, "_blank");
      };

  return (
    <div className="overlay">
      <div className="popup">
        <button className="close" onClick={(e) => getPopUpData(e)}>
          x
        </button>
        <img className="img content" src={apiDataP.urls.small} alt="img"></img>
        <div className="popup-contents">
          <div className="pop-grid"> 
            <img
              className="profile-img"
              src={apiDataP.user.profile_image.medium}
              alt="img"
            ></img>
          </div>
          <div className="pop-grid">
            {apiDataP.user.name} @{apiDataP.user.username}
          </div>
          <div className="pop-grid"></div>
          <div className="pop-grid"><Instagram fontSize="small"/>/{apiDataP.user.social.instagram_username} <br/>
          <Twitter fontSize="small" />/ {apiDataP.user.social.twitter_username}</div>

          <div className="pop-grid"></div>
          <div className="pop-grid"><ThumbUpOutlined fontSize="small" /> {apiDataP.likes} </div>
        </div>
        <div className="related-tags">
            Related tags
        </div>
        <div className="tag-card">
        {apiDataP.tags.length > 0 &&
            apiDataP.tags.map((tag, index)=> (
                <div key={index} className="tag"> {tag.title}</div>
            ))}
        </div>
        <div className="download-area">
            {/* <Link to="/files/myfile.pdf" target="_blank" download>Download</Link> */}
            <button className="download" >
                <CloudDownloadOutlined onClick={window.open(apiDataP.links.download,"_blank")} fontSize="small" /> Download
            </button>
        </div>
      </div>
    </div>
  );
}

export default PopUp;
