import React from 'react'
import PopUp from './PopUp';
import { useState } from 'react';

function Display({apiDataP, index}) {

    const [imagePop, setImagePop] = useState(false);

    console.log(apiDataP);
    console.log("hello");
    function getPopUpData(e){
        e.preventDefault();
        console.log("im in");
        setImagePop(!imagePop);
    }

    return (
        <div >   
            <div className="row-list"  key={index}>
                <img className="img" onClick={e => getPopUpData(e)} src={apiDataP.urls.thumb} alt="img"></img>
                {imagePop ? <PopUp getPopUpData={getPopUpData} index={index}></PopUp> : ""}
            </div> 
        </div> 
    )
}

export default Display
