import PopUp from './PopUp';

function Display({apiData}) {

    const [imagePop, setImagePop] = useState(false);

    console.log(apiData);
    console.log("hello");
    function getPopUpData(index){
        console.log("im in");
        console.log(apiData[index]);
        setImagePop(true);
    }

    return (
        <div className="display">   
            {apiData.length > 0 &&
            apiData.map((photo, index) => (
            <div className="row-list" onClick={getPopUpData(index)} key={index}>
                <img className="img"  src={photo.urls.thumb} alt="img"></img>
                {imagePop ? <PopUp></PopUp> : ""}
            </div> ))}
        </div> 
    )
}

export default Display
